svg.script:

let svg = document.documentElement;
const imgCount = 4;
let startY, currentY, scrollTop = 0, currentScrollY = 0, scrolling = false;
const imgContainer = document.getElementById('imgContainer');
svg.addEventListener('pointerdown', function(e) {
    startY = e.clientY;
    svg.setPointerCapture(e.pointerId);
});

svg.addEventListener('pointermove', function(e) {
    if (startY !== undefined) {
        currentY = e.clientY;
        let deltaY = currentY - startY;
        if (deltaY < 0 && !scrolling && Math.abs(currentScrollY) < imgCount - 1) {
            scrolling = true;
            currentScrollY -= 1;
            imgContainer.style.transform = 'translateY(' + (currentScrollY * 100) + 'vh)';
            setTimeout(() => {
                scrolling = false;
            }, 500);
        } else if (deltaY > 0 && !scrolling && currentScrollY < 0) {
            scrolling = true;
            currentScrollY += 1;
            imgContainer.style.transform = 'translateY(' + (currentScrollY * 100) + 'vh)';
            setTimeout(() => {
                scrolling = false;
            }, 500);
        }
    }
});
