svg.script:

let svg = document.documentElement;
let startY, currentY, scrollTop = 0;

svg.addEventListener('pointerdown', function(e) {
    startY = e.clientY;
    svg.setPointerCapture(e.pointerId);
});

svg.addEventListener('pointermove', function(e) {
    if (startY !== undefined) {
        currentY = e.clientY;
        let deltaY = currentY - startY;
        let images = document.querySelectorAll('image');
        images.forEach(function(img) {
            let currentY = parseFloat(img.getAttribute('y'));
            img.setAttribute('y', currentY + deltaY);
        });
    }
});

svg.addEventListener('pointerup', function(e) {
    if (startY !== undefined) {
        let deltaY = currentY - startY;
        scrollTop += deltaY;
        if (scrollTop > 0) scrollTop = 0;
        if (scrollTop < -900) scrollTop = -900;

        let images = document.querySelectorAll('image');
        images.forEach(function(img) {
            let currentY = parseFloat(img.getAttribute('y')) + deltaY;
            img.setAttribute('y', currentY);
        });

        startY = undefined;
        svg.releasePointerCapture(e.pointerId);
    }
});
